import React from 'react'
import { View, TouchableOpacity, Text, I18nManager, Platform } from 'react-native'
import Ionicons from 'react-native-vector-icons/FontAwesome'
import ImageLoad from '../RnImagePlaceH'
import { Icon } from 'native-base'
import theme from '../Theme.style'
import Timer from '../Timer'
export default CardOne = ({ props, widthPic, t, s, btnWidth }) => (
  <View
    style={{
      backgroundColor: theme.backgroundColor,
      width: widthPic,
      shadowOffset: { width: 1, height: 1 },
      shadowColor: theme.textColor,
      shadowOpacity: 0.3,
      elevation: 3,
      margin: 5,
      marginBottom: 5,
      borderRadius: 20 / 2
    }}>
    {/* /// ///////////////////////////////////////////////////// 2nd */}
    <View
      style={{
        backgroundColor: theme.backgroundColor,
        borderRadius: 20 / 2
      }}>
      {t.newMethod3(props, t) === 1 ? (
        t.props.recentViewedProducts && props.recent ? (
          <TouchableOpacity
            style={{
              width: btnWidth,
              shadowOffset: { width: 1, height: 1 },
              shadowColor: theme.textColor,
              shadowOpacity: 0.5,
              elevation: 3,
              position: 'absolute',
              bottom: 4,
              left: 5
            }}
            onPress={() => t.removeRecent(props, t)}>
            <View
              style={{
                alignItems: 'center',
                height: Platform.OS === 'android' ? 30 : 28,
                width: btnWidth,
                justifyContent: 'center',
                backgroundColor: theme.removeBtnColor
              }}>
              <Text
                style={{
                  color: theme.removeBtnTextColor,
                  fontSize: theme.mediumSize + 1,
                  fontWeight: '500'
                }}>
                {t.props.language2.Remove}
              </Text>
            </View>
          </TouchableOpacity>
        ) : props.removeButton ? (
          <TouchableOpacity
            style={{
              width: btnWidth,
              shadowOffset: { width: 1, height: 1 },
              shadowColor: theme.textColor,
              shadowOpacity: 0.5,
              elevation: 3,
              position: 'absolute',
              bottom: 4,
              left: 5
            }}
            onPress={() => t.removeWishlist(props, t)}>
            <View
              style={{
                alignItems: 'center',
                height: Platform.OS === 'android' ? 30 : 28,
                width: btnWidth,
                justifyContent: 'center',

                backgroundColor: theme.removeBtnColor
              }}>
              <Text
                style={{
                  color: theme.removeBtnTextColor,
                  fontSize: theme.mediumSize + 1,
                  fontWeight: '500'
                }}>
                {t.props.language2.Remove}
              </Text>
            </View>
          </TouchableOpacity>
        ) : null
      ) : null}
      <View
        style={{
          backgroundColor: theme.backgroundColor,
          borderRadius: 20 / 2
        }}>
        <TouchableOpacity
          style={{
            flex: 2,
            borderRadius: 20 / 2
          }}
          onPress={() =>
            props.navigation.push('ProductDetails', {
              objectArray: props.objectArray //
            })
          }>
          <ImageLoad
            key={props.objectArray.id}
            style={{
              height: widthPic,
              width: widthPic,
              backgroundColor: 'rgb(236, 236, 236)',
              borderRadius: 20 / 2
            }}
            source={{ uri: theme.url + '/' + props.objectArray.products_image }}>
          </ImageLoad>
          {t.checkProductNew(props) ? (
            <View
              style={{
                backgroundColor: '#1473E6',
                zIndex: 6,
                textAlign: 'center',
                padding: 5,
                borderTopLeftRadius: props.objectArray.on_sale ? 0 : 20 / 2,
                borderBottomRightRadius: 20 / 2,
                borderTopRightRadius: props.objectArray.on_sale ? 20 / 2 : 0,
                paddingRight: 7,
                left: 0,
                top:
                    props.objectArray.on_sale && props.objectArray.featured
                      ? 55
                      : props.objectArray.on_sale
                        ? 27
                        : 0,
                alignItems: 'center',
                justifyContent: 'center',
                position: 'absolute',
                fontWeight: '400',
                borderRadius: 2
              }}>
              <Text style={{ color: '#fff', fontSize: 11 }}>
                {t.props.language2.New}
              </Text>
            </View>
          ) : null}
          {props.objectArray.on_sale ? (
            <View
              style={{
                backgroundColor: '#D50000',
                zIndex: 6,
                textAlign: 'center',
                padding: 5,
                paddingRight: 7,
                borderBottomRightRadius: 20 / 2,
                borderTopLeftRadius: 20 / 2,
                left: 0,
                top: 0,
                alignItems: 'center',
                justifyContent: 'center',
                position: 'absolute',
                fontWeight: '400',
                borderRadius: 2
              }}>
              <Text style={{ color: '#fff', fontSize: 11 }}>
                {t.props.language2.Sale}
              </Text>
            </View>
          ) : null}
          {props.objectArray.featured ? (
            <View
              style={{
                backgroundColor: '#047800',
                zIndex: 6,
                textAlign: 'center',
                padding: 5,
                borderTopLeftRadius: props.objectArray.on_sale ? 0 : 20 / 2,
                borderBottomRightRadius: 20 / 2,
                borderTopRightRadius: props.objectArray.on_sale ? 20 / 2 : 0,
                left: 0,
                top:
                    !props.objectArray.on_sale && !t.checkProductNew(props)
                      ? 0
                      : 28,
                alignItems: 'center',
                justifyContent: 'center',
                position: 'absolute',
                fontWeight: '400',
                borderRadius: 2
              }}>
              <Text style={{ color: '#fff', fontSize: 11 }}>
                {t.props.language2.Featured}
              </Text>
            </View>
          ) : null}
        </TouchableOpacity>

        <View
          style={{
            flex: 1,
            justifyContent: 'center',
            alignSelf: 'center',
            padding: 1,
            margin: 1,
            marginBottom: 0,
            paddingBottom: 1,
            paddingTop: 0,
            marginTop: 0,
            width: widthPic
          }}>
          <Text
            style={{
              fontSize: theme.mediumSize - 1,
              fontFamily: 'Roboto',
              writingDirection: I18nManager.isRTL ? 'rtl' : 'ltr',
              color: theme.textColor,
              margin: 0,
              padding: 5,
              paddingTop: Platform.OS === 'android' ? 2 : 3,
              paddingBottom: 0,
              marginBottom: 0,
              fontWeight: '400',
              width: widthPic - 20
            }}
            numberOfLines={1}>
            {props.objectArray.products_name}
          </Text>

          <View
            style={{
              backgroundColor: 'transparent',
              justifyContent: 'space-between',
              alignItems: 'center',
              flexDirection: 'row',
              marginTop: 2,
              marginBottom: props.objectArray.flash_price === undefined ? 0 : 2,
              padding: 5,
              paddingLeft: 6,
              paddingTop: 0,
              paddingBottom: 3
            }}>
            {props.objectArray.flash_price !== undefined ? (
              <View style={{ flexDirection: 'row' }}>
                {t.priceFun(
                  theme.mediumSize - 1,
                  props.objectArray.products_price,
                  'line-through'
                )}
                {t.priceFun(
                  theme.mediumSize - 1,
                  props.objectArray.flash_price,
                  'none'
                )}
              </View>
            ) : null}

            {props.objectArray.flash_price === undefined ? (
              <View style={{ flexDirection: 'row' }}>
                {props.objectArray.discount_price === null
                  ? t.priceFun(
                    theme.mediumSize - 1,
                    props.objectArray.products_price,
                    'none'
                  )
                  : null}
                {props.objectArray.discount_price !== null
                  ? t.priceFun(
                    theme.mediumSize - 1,
                    props.objectArray.products_price,
                    'line-through'
                  )
                  : null}
                {props.objectArray.discount_price !== null &&
                props.objectArray.discount_price !== undefined
                  ? t.priceFun(
                    theme.mediumSize - 1,
                    props.objectArray.discount_price,
                    'none'
                  )
                  : null}
              </View>
            ) : null}
            {props.objectArray.flash_price === undefined ? (
              <View>
                {t.checkWishList(props, t) === 1 ? (
                  props.removeButton ? (
                    <Icon
                      style={{
                        color: '#DE004B',
                        fontSize: 17
                      }}
                      active
                      name='heart'
                      onPress={() => {
                        if (t.newMethod3(props, t) !== 1) {
                          t.removeWishlist(props, t)
                        }
                      }}
                    />
                  ) : (
                    <Icon
                      style={{
                        color: '#DE004B',
                        fontSize: 17
                      }}
                      active
