  getProducts () {
    const data = []
    for (const v of this.props.cartItems2.cartItems.cartProductArray) {
      const obj = {
        quantity: v.quantity,
        product_id: v.product_id,
        total: v.total.toString(),
        price: v.price.toString()
      }
      if (v.variation_id) Object.assign(obj, { variation_id: v.variation_id })
      data.push(obj)
    }
    return data
  }

